<?php  include 'config.php';		$HT = $_SESSION['HT'];		$user = $_POST['name'];	$password = $_POST['password'];  $password2 = $_POST['password2'];	$mess = '';		if ($password == $password2) {		if ($HT->getClub()->getUserId() > 0) {			if (CoachDB::getCoachNaamExists($HT->getClub()->getUserId(), $user)) {			  $mess = $language['coachNameExists'];			}			else			{				$coach = CoachDB::getCoach($HT->getClub()->getUserId());						$coach->setDSUserName($user);				$coach->setDSPassword(crypt($password));						CoachDB::updateCoach($coach);			}		}	}	else {		$mess = $language['differentPasswords'];			}		if ($mess <> '') {    header('Location: '.$config['url'].'/callbackHT.php?oauth_token='.$_POST['oauth_token'].'&oauth_verifier='.$_POST['oauth_verifier'].'&mess='.$mess); 	}	else	{	  header('Location: '.$config['url']);	}?>		